insert into Products values ('Passive Me, Aggresive You', 'Дебютный полноформатный студийный альбом новозеландской группы альтернативного рока The Naked and Famous, вышел в 2010 году в Новой Зеландии.', 2010, 300, 'The Naked and Famous', 'Альтернатива', null, null, 0, null, null)
insert into Products values ('In Rolling Waves', 'Второй студийный альбом новозеландской пост-панк группы The Naked and Famous. Был выпущен 13 сентября 2013 года.', 2013, 320, 'The Naked and Famous', 'Альтернатива', null, null, 0, null, null)
insert into Products values ('Infinite', 'Дебютный студийный альбом американского рэпера Eminem’а изданный 12 ноября 1996 года на лейбле Web Entertainment. Это первый релиз записанный под псевдонимом Eminem (до этого Маршалл Мэтерс выступал как M&M). Альбом был записан на студии «Bassmint Productions» принадлежащей Bass Brothers. ', 1996, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('The Slim Shady LP', 'Второй студийный альбом американского рэпера Эминема, выпущенный в 1999 году на лейбле Dr. Dre — Aftermath Entertainment. Первый альбом Эминема, который был выпущен на крупном лейбле звукозаписи. Альбом оказался успешным как в плане продаж, так и по оценкам критиков. ', 1999, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('The Eminem Show', 'Четвёртый студийный альбом американского рэпера Эминема и 1-й его сольный альбом, изданный на собственном лейбле Shady Records в 2002 году.', 2002, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('Encore', 'Пятый студийный альбом американского рэпера Эминема и 2-й его сольный альбом, изданный на собственном лейбле Shady Records в 2004 году. Выход альбома был изначально запланирован на 16 ноября 2004 года, но был перенесён на 12 ноября из-за утечки альбома в интернет.', 2004, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('Relapse', 'Шестой студийный альбом американского рэпера Эминема, издан 18 мая 2009 года на лейбле Universal. В первую неделю продаж Relapse установил рекорд этого года и разошёлся тиражом более 1 000 000 копий по всему миру. Также альбом стал номером 1 в чарте Billboard с 608 000 копиями за первую неделю в США.', 2009, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('Recovery', 'Седьмой студийный альбом американского рэпера Eminem, издан в 2010 году. Самый продаваемый альбом 2010 года в США. Выход Recovery был подтвержден во время пресс-релиза Eminem’а, состоявшегося 5 марта 2009, и альбом был первоначально назван Relapse 2. Все было намечено на выпуск альбома продолжения в 2009, после выпуска альбома Relapse, но релиз был перенесен на следующий 2010 год.', 2010, 150, 'Eminem', 'Реп', null, null, 0, null, null)
insert into Products values ('Спутник', 'Дебютный сольный студийный альбом российского рэпера LOne, выпущенный 23 апреля 2013 на лейбле Black Star Inc.', 2013, 100, 'LOne', 'Реп', null, null, 0, null, null)
insert into Products values ('Одинокая Вселенная', 'Центральная пластинка трилогии рассказывает о моральном и физическом состоянии человека, пребывающего на орбите. LOne продолжает вести слушателя по космосу и мотивировать на новые свершения в жизни, приводя примеры из собственной биографии и иногда включая «бэнгерские» способности. ', 2014, 100, 'LOne', 'Реп', null, null, 0, null, null)
insert into Products values ('Автолюбитель', 'Новый EP, в котором рэпер словно возвращается во времена группы Marselle. Размеренные, мелодичные биты, сэмплириование – одним словом, все то, что мы привыкли слышать в исполнении Левана до перехода на Black Star. Не хватает только куплетов Нэла.', 2015, 100, 'LOne', 'Реп', null, null, 0, null, null)
insert into Products values ('Побег из Шоушенка', 'Успешный банкир Энди Дюфрейн обвинен в убийстве собственной жены и ее любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решетки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, вооруженный живым умом и доброй душой, отказывается мириться с приговором судьбы и начинает разрабатывать невероятно дерзкий план своего освобождения.', 1994, 300, null, null, 'Френк Дарабонт', 'Драма', 1, null, null)
insert into Products values ('Зеленая миля', 'Обвиненный в страшном преступлении, Джон Коффи оказывается в блоке смертников тюрьмы «Холодная гора». Вновь прибывший обладал поразительным ростом и был пугающе спокоен, что, впрочем, никак не влияло на отношение к нему начальника блока Пола Эджкомба, привыкшего исполнять приговор.', 1999, 300, null, null, 'Френк Дарабонт', 'Драма', 1, null, null)
insert into Products values ('Форрест Гамп', 'От лица главного героя Форреста Гампа, слабоумного безобидного человека с благородным и открытым сердцем, рассказывается история его необыкновенной жизни. Фантастическим образом превращается он в известного футболиста, героя войны, преуспевающего бизнесмена. Он становится миллиардером, но остается таким же бесхитростным, глупым и добрым. Форреста ждет постоянный успех во всем, а он любит девочку, с которой дружил в детстве, но взаимность приходит слишком поздно.', 1994, 300, null, null, 'Роберт Земекис', 'Драма', 1, null, null)
insert into Products values ('Список Шиндлера', 'Фильм рассказывает реальную историю загадочного Оскара Шиндлера, члена нацистской партии, преуспевающего фабриканта, спасшего во время Второй мировой войны почти 1200 евреев.', 1993, 250, null, null, 'Стивен Спилберг', 'Драма', 1, null, null)
insert into Products values ('1+1', 'Пострадав в результате несчастного случая, богатый аристократ Филипп нанимает в помощники человека, который менее всего подходит для этой работы, — молодого жителя предместья Дрисса, только что освободившегося из тюрьмы. Несмотря на то, что Филипп прикован к инвалидному креслу, Дриссу удается привнести в размеренную жизнь аристократа дух приключений.', 2011, 260, null, null, 'Оливье Накаш', 'Драма', 1, null, null)
insert into Products values ('Начало', 'Кобб — талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил. ', 2010, 300, null, null, 'Кристофер Нолан', 'Фантастика', 1, null, null)
insert into Products values ('Престиж', 'Роберт и Альфред — фокусники-иллюзионисты, которые на рубеже XIX и XX веков соперничали друг с другом в Лондоне. С годами их дружеская конкуренция на профессиональной почве перерастает в настоящую войну.', 2006, 300, null, null, 'Кристофер Нолан', 'Фантастика', 1, null, null)
insert into Products values ('Интерстеллар', 'Когда засуха приводит человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и переселить человечество на другую планету.', 2014, 300, null, null, 'Кристофер Нолан', 'Фантастика', 1, null, null)
insert into Products values ('Изгой', 'Герой этого фильма, Чак Ноланд, — сотрудник всемирно известной службы доставки «Федерал Экспресс». Чак — скрупулезный практик и неисправимый педант. Жизнь Ноланда, высокопоставленного инспектора международных отделений «ФедЕкс» расписана по минутам. И этих драгоценных минут катастрофически не хватает ни на личную жизнь, ни на любимую женщину…', 2000, 300, null, null, 'Роберт Земекис', 'Драма', 1, null, null)
